const SoundPlayer = ({ sounds }) => {
  return (
    <div>
      <p className="w-full font-serif text-white my-2 text-center">
        Nature Sounds
      </p>
      <div className="flex flex-wrap justify-center  gap-4 p-4">
        {sounds.map((sound, index) => (
          <SoundBox key={index} sound={sound} />
        ))}
      </div>
    </div>
  );
};

const SoundBox = ({ sound }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.3);
  const audioRef = useRef(null);

  const togglePlayPause = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  const handleVolumeChange = (e) => {
    const newVolume = e.target.value;
    setVolume(newVolume);
    audioRef.current.volume = newVolume;
  };

  return (
    <div>
      <div className="flex  flex-col gap-y-1 w-28 items-center  bg-white/10 backdrop-blur-lg  p-4 rounded-lg shadow-lg ">
        <button
          className={`flex items-center justify-center w-16 h-16 mb-4 rounded-full ${
            isPlaying ? "bg-green-600 text-white" : "text-white/70"
          }  active:scale-95`}
          onClick={togglePlayPause}
        >
          {/* Example of Custom Icon */}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-8 hover:scale-110"
            fill={`${isPlaying ? "white" : "#ffffffb3"}`}
            viewBox={sound.view}
          >
            <path d={sound.icon} />
          </svg>{" "}
        </button>
        <div className="text-center text-white text-sm font-serif">
          {sound.name}
        </div>
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
          className={`${isPlaying ? "block" : "hidden"} w-16 mt-2 accent-white`}
        />
        <audio ref={audioRef} src={sound.url} loop />
      </div>
    </div>
  );
};

